describe WikipediaService do
  context "get taxon name in chinese" do
    it "it has a Chinese name" do
      stub_request(:get, "https://zh.wikipedia.org/zh-tw/Abies%20kawakamii").
        with(
          headers: {
            'Accept' => '*/*',
            'Accept-Encoding' => 'gzip;q=1.0,deflate;q=0.6,identity;q=0.3',
            'User-Agent' => 'Faraday v1.3.0'
          }).
        to_return(status: 200, body: response_body_with_chinese, headers: {})


      taxon_name = "Abies kawakamii"
      name = WikipediaService.get_chinese_taxon_name(taxon_name)
      expect(name).to eq "台灣冷杉"
    end

    it "it does not has a Chinese name" do
      stub_request(:get, "https://zh.wikipedia.org/zh-tw/Tsuga%20chinensis%20formosana").
        with(
          headers: {
            'Accept' => '*/*',
            'Accept-Encoding' => 'gzip;q=1.0,deflate;q=0.6,identity;q=0.3',
            'User-Agent' => 'Faraday v1.3.0'
          }).
        to_return(status: 200, body: reponse_body_without_chinese, headers: {})

      taxon_name = "Tsuga chinensis formosana"
      name = WikipediaService.get_chinese_taxon_name(taxon_name)
      expect(name).to eq taxon_name
    end


